package org.smart4j.chapter2.util;import org.apache.commons.lang3.StringUtils;/** * @ClassName StringUtil * @Description TODO * @Author mac * @Date 2019-05-03 12:00 AM * Version 1.0 **/public class StringUtil {    /**     * 判断字符串是否为空     */    public static boolean isEmpty(String str) {        if (str != null) {            str = str.trim();        }        return StringUtils.isEmpty(str);    }    /**     * 判断字符串是否非空     */    public static boolean isNotEmpty(String str) {        return !isEmpty(str);    }}